sc_cat_item_producer.script:
/** This script is executed before the Record is generated
 * `current`- GlideRecord produced by Record Producer
 * Don't use `current.update()` or `current.insert()` as the record is generated by Record Producer
 * Don't use `current.setValue('sys_class_name', 'xxx')` as this will trigger reparent flow and can cause data loss
 * Avoid `current.setAbortAction()` and generate a separate record
 * Use `producer.var1` to access variables
 */
var currentSysId = current.sys_id;
var claimant = {
    representative: producer.representative_name,
    representative_reference: producer.representative_reference_number,
    name: producer.claimant_name,
    insurer_policy_number: producer.claimant_reference_number,
    insurer_organization_name: producer.claimant_insurer,
    vehicle_vrn: producer.claimant_vrn
};


//Claimant Details
if (producer.representative_name) {
    var claimantGR = new GlideRecord('x_1523132_credit_0_claimant_details');
    claimantGR.initialize();
    claimantGR.representative = claimant.representative;
    claimantGR.representative_reference = claimant.representative_reference;
    claimantGR.name = claimant.name;
    claimantGR.insurer_policy_number = claimant.insurer_policy_number;
    claimantGR.insurer_organization_name = claimant.insurer_organization_name;
    claimantGR.vehicle_vrn = claimant.vehicle_vrn;
    claimantGR.is_active = true;
    claimantGR.claim_id = currentSysId;
    claimantGR.insert();
}

//Defendant Details
var defendantGR = new GlideRecord('x_1523132_credit_0_defendant_details');
defendantGR.initialize();
defendantGR.name = producer.defendant_name;
defendantGR.vehicle_vrn = producer.defendant_vrn;
defendantGR.insurer = producer.defendant_insurer_name;
defendantGR.insurer_reference = producer.defendant_insurance_reference;
defendantGR.is_active = true;
defendantGR.claim_id = currentSysId;
defendantGR.insert();

//Accident details
var accidentGR = new GlideRecord('x_1523132_credit_0_accident_details');
accidentGR.initialize();
accidentGR.claim_id = currentSysId;
accidentGR.is_active = true;
accidentGR.damage_claimed = producer.damage_claimed;
accidentGR.vehicle_condition = producer.vehicle_condition;
accidentGR.need_type = producer.need_type;
accidentGR.dealing_party = producer.dealing_party;
accidentGR.damage_claimant_representative = claimantGR.representative;
accidentGR.insert();


//Repair Details
var repairGR = new GlideRecord('x_1523132_credit_0_repair_details');
repairGR.repair_facility_name = producer.repair_facility_name;
repairGR.claim_id = currentSysId;
repairGR.insert();

//Vehicle Details
var vehicleGR = new GlideRecord('x_1523132_credit_0_vehicle_details');
vehicleGR.vehicle_vrn = producer.claimant_vrn;
vehicleGR.claim_number = currentSysId;
vehicleGR.insert();

//Liability Details
var liabilityGR = new GlideRecord('x_1523132_credit_0_liability_details');
liabilityGR.claim_id = currentSysId;
liabilityGR.insert();

//Negotiation Details
var negotiationGR = new GlideRecord('x_1523132_credit_0_negotiation_details');
negotiationGR.claim_id = currentSysId;
negotiationGR.insert();

//Payment Details
var paymentGR = new GlideRecord('x_1523132_credit_0_payment_details');
paymentGR.claim_id = currentSysId;
paymentGR.insert();

//Hire Details
var hireGR = new GlideRecord('x_1523132_credit_0_hire_details');
hireGR.claim_number = currentSysId;
hireGR.insert();


//Update in Claim Details
current.accident_id = accidentGR.sys_id;
current.claimant_id = claimantGR.sys_id;
current.defendant_id = defendantGR.sys_id;
current.repair_id = repairGR.sys_id;
current.vehicle_id = vehicleGR.sys_id;
current.liability_id = liabilityGR.sys_id;
current.negotiation_id = negotiationGR.sys_id;
current.payment_id = paymentGR.sys_id;
current.hire_id = hireGR.sys_id;
