sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var json_value = JSON.stringify(inputs.response);
var replace = json_value.replaceAll('"', '');
var replace1 = replace.replaceAll('{','[');
var replace2 = replace1.replaceAll('}',']');
outputs.json_output = replace2.replaceAll('\\','');
gs.info('Response Message : ' + outputs.json_output);
})(inputs, outputs);

